//Ethan Alexander Shulman 2015

#define iterations 2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	float res = 1.;
    for (int i = 0; i < iterations; i++) {
        res += cos(uv.y*12.345 - iTime*4. + cos(res*12.234)*.2 + cos(uv.x*32.2345 + cos(uv.y*17.234)) ) + cos(uv.x*12.345);
    }

	vec3 c = mix(vec3(1.,0.,0.),
                 vec3(.6,.2,.2),
                 cos(res+cos(uv.y*24.3214)*.1+cos(uv.x*6.324+iTime*4.)+iTime)*.5+.5);

    c = mix(c,
            vec3(0.),
            clamp( (length(uv-.5 + cos(iTime+uv.yx*4.34+uv.xy*res)*.1 )*5.-.4) , 0., 1.) );

    fragColor = vec4(c,length(c));
}
