void mainImage( out vec4 color, in vec2 pixCoords )
{
    //********* CAMERA
    float zoom = (iResolution.x / 10.0);
    float cameraX = 5.0;
    float cameraY = 2.0;
    float x = (pixCoords.x / zoom) - cameraX;
    float y = (pixCoords.y / zoom) - cameraY;



    //********* GRID
    float lineSemiWidth = 0.01;

    if (abs(x) < lineSemiWidth || abs(y)<lineSemiWidth) {
		color = vec4(1.0, 0.0, 0.0, 1.0);
        return;
    }

    if (mod(x, 1.0) < lineSemiWidth || mod(y, 1.0) < lineSemiWidth) {
		color = vec4(0.5, 0.5, 0.5, 1.0);
        return;
    }




    float PI             = 3.1415;
    float hauteurMoyenne = 0.0;
    float amplitude      = 1.0;
    float phase          = 4.0 + 5.0 * cos(iTime);
    float periode        = 3.0;
    float fx = hauteurMoyenne + cos(x * 2.0 * PI / periode + phase) * amplitude;

    if (y < fx) {
        color = vec4(0.0, 0.0, 1.0, 1.0);
        return;
    }




    float fx2 = 1.0 + cos(2.5 * (x + iTime)) * 0.3;

    if (y < fx2) {
        color = vec4(0.6, 0.6, 1.0, 1.0);
        return;
    }



    //********* BACKGROUND
    color = vec4(1.0);

}
