float sceneSDF(vec3 p, float noiseVal)
{
	const vec3 n = vec3(0., 1., 0.);
    p.y += noiseVal;
	return dot(p,n);
}

vec4 color(vec2 uv, float noiseVal) 
{    
    const float detail = .25;
    uv *= detail;

    vec2 cell = floor(uv);
    vec2 frac = fract(uv);
    float minDistance = 1.;

    for (int i = -1; i <= 1; i++) {
        for (int j = -1; j <= 1; j++) {
        	vec2 cellDir = vec2(float(i), float(j));
            vec2 randPoint = random2(cell + cellDir);
            randPoint += noise(uv);
            randPoint = 0.5 + 0.5 * sin(iTime * 5. + 6.2831 * randPoint);
            minDistance = min(minDistance, length(cellDir + randPoint - frac));
        }
    }

    return mix(mix (PEAK_COLOR, VALLEY_COLOR, noiseVal / HEIGHT), vec4(1.), pow(minDistance, 1.5));
}

vec4 raymarch(vec2 uv, vec3 eye)
{
    vec3 pos = eye;
    vec3 dir = normalize(vec3(uv.x, uv.y, -1.));
    float dist = 0.;
    float noiseVal = 0.;

    for (int i = 0; i < STEPS; i++) 
    {
    	noiseVal = HEIGHT * (snoise(vec4(pos * NOISINESS, iTime)) + 1.) / 2.;
        dist = sceneSDF(pos, noiseVal);
        pos += dir * dist;
    }

    if (dist < EPSILON) {
        return color(pos.xz, noiseVal);
    }

    return vec4(0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec3 eye = vec3(0., 6., -iTime * SPEED);

    fragColor.rgb = raymarch(uv, eye).rgb;
}
