struct Pixel {
    vec2 coordinate;
    vec3 color;
};

struct Circle {
    float radius;
    vec2 position;
    vec3 color;
};

const vec3 background = vec3(0.1, 0.2, 0.4);

Pixel initPixel(in vec3 color, in vec2 fragCoord) {
    Pixel pixel = Pixel(
        2.0 * fragCoord.xy / iResolution.xy - 1.0,                          /* coordinate */
        color                                                                   /* color */
    );
    float ratio = iResolution.x / iResolution.y;
    if (ratio > 1.0) {
        pixel.coordinate.x *= ratio;
    } else {
        pixel.coordinate.y /= ratio;
    }
    return pixel;
}

void drawCircle(inout Pixel pixel, in Circle circle) {
    if (length(circle.position - pixel.coordinate) < circle.radius) {
        pixel.color = circle.color;
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    Pixel pixel = initPixel(background, fragCoord);

    Circle circle = Circle(
        0.5,                                                                    /* radius */
        vec2(0.0, 0.0),                                                         /* position */
        vec3(0.2, 0.8, 0.2)                                                     /* color */
    );

    drawCircle(pixel, circle);

    fragColor = vec4(pixel.color, 1.0);
}
