void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    ivec2 coord = ivec2(fragCoord);
    vec4 data = texelFetch(iChannel0, ivec2(0),0);
	fragColor = texelFetch(iChannel0,coord,0);

    bool draw = iMouse.z > 0.0;

    if (draw) {
        float len = length(fragCoord.xy-iMouse.xy);

        if (fragColor.a > len) {
            fragColor.a = len;
            fragColor.xyz = fract(sin(vec3(data.x)*vec3(1,2,3))*9.);
            if (len < 4.0) {
                fragColor.xyz = vec3(1,0,0);
            }
        }
        //fragColor = vec4(min(fragColor.x,len));
    }

    if (iMouse == vec4(0)) {
        float len = 1e10;
        vec3 col = vec3(0);
        for (int i = 0; i < 10; i++) {
            float len2 = length(fragCoord.xy-iResolution.xy*(0.5+0.5*sin(iTime*0.01*vec2(1,2.563)*float(i)+float(i*i))));

            if (len2 < len) {
                len = len2;
                col = fract(sin((float(i)+0.5)*vec3(1,2,3))*9.);
            }

        }
        fragColor.xyz = col;
        if (len < 4.0) {
            fragColor = vec4(1,0,0,1);
        }
	}
}

//line signed distance field by iq
float line( vec2 p, vec2 a, vec2 b ) 
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 coord = ivec2(fragCoord);
    fragColor = texelFetch(iChannel0,coord,0);

    vec4 data = texelFetch(iChannel0, ivec2(0),0);

    if (fragColor == vec4(0)) {
        fragColor = vec4(1000000);
        return;
    }

    bool placecell;
    bool draw = texelFetch(iChannel1,ivec2(32,0),0).x>0.5;

    if (draw) {
        placecell = iMouse.z > 0.0;
    } else {
        placecell = iMouse.z < 0.0 && data.w > 0.0;
    }
    if (placecell) {
        float len;
        if (draw&&(abs((data.x+iTimeDelta)-iTime)>0.01))
            len = line(fragCoord.xy,iMouse.xy,data.yz);
        else
            len = length(fragCoord.xy-abs(iMouse.xy));
        if (fragColor.a > len) {
            fragColor.a = len;
            fragColor.xyz = fract(sin(data.x*vec3(1,2,3))*9.);
            if (len < 4.0) {
                fragColor.xyz = vec3(1,0,0);
            }
        }
        //fragColor = vec4(min(fragColor.x,len));
    }

    if (coord == ivec2(0)) {
        if (iMouse.z > 0.0) {
            fragColor.x = data.x;
        } else {
            fragColor.x = iTime;
        }
        fragColor.yzw = iMouse.xyz;
    } else if (iFrame == 1) {
        fragColor = vec4(1000000);
    }
}
