float PI = atan(1.0) * 4.0;
float TAU = atan(1.0) * 8.0;


float circle (vec2 center, vec2 fragCoord, float radius, float pixelWidth) {
    return smoothstep(radius + pixelWidth, radius - pixelWidth, length(fragCoord - center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float pixelWidth = 1.0 / min(iResolution.x, iResolution.y);
	vec2 pos = (fragCoord - 0.5 * iResolution.xy) * pixelWidth;
    vec2 mouse = (iMouse.xy - 0.5 * iResolution.xy) * pixelWidth;

    if(iMouse.xy == vec2(0.0)) {
        mouse += 0.5 * iResolution.xy * pixelWidth;
    }

    float phase = -(iTime / 2.0) * TAU;
    float radius = 0.05 + (cos(phase) * 0.01);
    fragColor.r += circle(vec2(cos(phase), sin(phase)) *  0.15 + mouse, pos, radius, pixelWidth);
    fragColor.g += circle(vec2(0.0) + mouse, pos, radius, pixelWidth);
    fragColor.b += circle(vec2(cos(phase), sin(phase)) * -0.15 + mouse, pos, radius, pixelWidth);
}
