void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float zoom = (0.5 + 0.5 * sin(iTime));

    /*

		javascript function:

			function F(uv, z)
            {
                // u: (uv.u - 0.5) * z + 0.5, _v: (uv.v - 0.5) * z + 0.5
                var obj = {}
                obj.u = (uv.u - 0.5) * z + 0.5;
                obj.v = (uv.v - 0.5) * z + 0.5;
                return obj;
            }

		let be 'a' = UV.u - 0.5
		let be 'b' = UV.v - 0.5
		let be 'z' zoom factor
		let be fx(z) = a * z + 0.5
		let be fy(z) = b * z + 0.5
		let be F(z) = <fx(z), fy(z)>

		When UV = <1, 1>

			UV.u = fx(0) = 0.5
			UV.u = fx(1) = 1

			UV.v = fy(0) -> 0.5
			UV.v = fy(1) -> 1

			F(0) = UV = <0.5 , 0.5>
			F(1) = UV = <1 , 1>

		When UV = <0, 0>

			F(0) = UV = <0.5, 0.5>
			F(1) = UV = <0, 0>

		When UV = <1, 0>

			F(0) = UV = <0.5, 0.5>
			F(1) = UV = <0, 0>

	*/    

    vec2 scaleCenter = vec2(0.5);
    uv = (uv - scaleCenter) * zoom + scaleCenter;

    vec4 texel = texture(iChannel0, uv);

	fragColor = texel;
}
