void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 pos = ( fragCoord.xy / iResolution.x ) - vec2(0.5, 0.5 * (iResolution.y / iResolution.x));

	float r = length(pos);
	float a = atan(pos.y, pos.x);
	float c = pow(sin(a * 68.0 + 32.0*iTime * sin(32.0*r)/abs(sin(32.0*r))) * 0.5 + 0.5, 2.0) * pow(sin(r * 32.0), 2.0) * r;

	vec4 color = vec4(0.0, 0.0, 0.15, 0.0);
	color += vec4(2.0 * c, 2.0 * c, 2.0 * c, 1.0 * c);

	fragColor = color;
}
