void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoomOutFactor = 90.0f;
    float heartBeatFreq = 5.0f;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 xy = zoomOutFactor * ( 2.0f * uv - 1.0f);
	float dist_from_center = length(xy);
    vec3 bg_color =
        mix(vec3(1.0, 0.5, 0.6),
            vec3(0.5, 0.0, 0.1),
            dist_from_center / zoomOutFactor);
    xy.x *= 2.0 / mix(1.0, 5.0 + abs(sin(heartBeatFreq * iTime)), uv.y);
    xy.y /= 2.5 + abs(cos(heartBeatFreq * iTime))/2.0;
    float a = 5.0;
    float r = length(xy);
    float dist = r - 2.0 * a * (1.0 - xy.y / r);
    vec3 glow = mix(vec3(1.0),
                    bg_color, min(1.0, max(0.0, dist) / zoomOutFactor));
    vec3 c = mix(bg_color, glow, 0.45);
    fragColor = vec4(c + abs(dist) * (1.0 - step(0.0, dist)), 1.0);
}
