void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    rotate(PI * .75 + sin(rotationSpeed * iTime) * .5);

    vec3 camPos = vec3(0, 0, 1000);
	vec3 screenPos = vec3(fragCoord.x - iResolution.x * .5, fragCoord.y - iResolution.y * .5, 200);
    vec3 ray = normalize(screenPos - camPos);

    vec3 p = screenPos;

    for (int i = 0; i < 300; i++) {
        vec4 color = intersect(p);

        if (color != vec4(0, 0, 0, 0)) {
            fragColor = color * (1.0 - sqrt(distance(p, screenPos)) * .05) * 2.0;
            return;
        }

        p += ray;
    }

    fragColor = vec4(0, 0, 0, 0);
}
