// recommandation:            (replace 8 by 15 for double precision )
// #define tanh(x) ( abs(x) > 8. ? sign(x) : tanh(x) )

//                                   not Nan  
#define test(f)   vec4( f<=2., f==0., (f==f), 0)

#define sep vec4(0,0,1,0)

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
	float x = U.x + U.y*iResolution.x;

    U /= iResolution.xy;
    O =   U.y > .8
          ?   U.x < .33 ? test( tanh(87.+min(0.,iTime)) )
            : U.x < .34 ?  sep
            : U.x < .66 ? test( tanh(88.+min(0.,iTime)) )
            : U.x < .67 ?  sep
            :             test( tanh(89.+min(0.,iTime)) )
        : U.y > .79 ? sep
        : U.y > .6  
          ?   U.x < .33 ? test( tanh(87.) )
            : U.x < .34 ?  sep
            : U.x < .66 ? test( tanh(88.) )
            : U.x < .67 ?  sep
            :             test( tanh(89.) )
        : U.y > .59 ? sep
        : test( tanh(x) );

}
