void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-iResolution.xy/2.0) / iResolution.x;
    vec3 color = vec3(0.0);
    float lines = 600.0;
    float gap = 6.0;
    float speed = 0.010;
    const float num = 8.0;
    for(float i=0.0; i<num; i++){
        float angle = iTime*(i+1.0)*speed;
    	float c = floor(mod((cos(angle)*uv.x+sin(angle)*uv.y)*lines, gap));
        if(c==0.0)
        {
            color += 1.0;
        }
    }
    //vec3 color = vec3(0.0);
	fragColor.rgb = 1.0-color;
}
