vec3 CHROMA_KEY = vec3(0.051, 0.635, 0.149);

vec2 get_derivs(in vec2 p) 
{ 
    float offset = 1.75;
    vec2 texel_size = vec2(1.0) / iChannelResolution[0].xy;

    float a = texture(iChannel0, p).r; // Note red channel here.
    vec2 d = 
        vec2(texture(iChannel0, p - vec2(offset * texel_size.x, 0)).g, 
             texture(iChannel0, p - vec2(0, offset * texel_size.y)).g);
    d = (d - vec2(a, a)) / (offset * texel_size);
    return d;
}

vec3 sample_bg(in vec2 p)
{
    float b = 2.2;
    vec2 texel_size = vec2(1.0) / iChannelResolution[1].xy;
    // Combine mip mapping with box blur for cheapo blur.
	return (texture(iChannel1, -p, b).rgb 
          + texture(iChannel1, -p - 1.3 * texel_size, b).rgb
          + texture(iChannel1, -p + 1.3 * texel_size, b).rgb
          + texture(iChannel1, -p + 1.3 * vec2( texel_size.x, -texel_size.y), b).rgb
          + texture(iChannel1, -p + 1.3 * vec2(-texel_size.x,  texel_size.y), b).rgb
          ) * 0.2;
}

vec3 sample_vandamme(in vec2 p)
{
    return texture(iChannel0, p).rgb;
}

vec3 composite(in vec3 fg, in vec2 fg_derivs, in vec3 bg)
{
    return mix(fg, bg, clamp(1.9 * fg.g - fg.r - fg.b, 0.0, 0.5) * 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 fg = sample_vandamme(uv);
    vec3 bg = sample_bg(uv);
    vec2 fg_derivs = get_derivs(uv);

	fragColor = vec4(composite(fg, fg_derivs, bg), 1.0);
    //fragColor = vec4(fg_derivs.xyx, 1.0) * 0.02;
}
