const int iter = 1000;
float scale = 3.;
vec2 center = vec2(1.001105,0.300717);

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    scale = 1./(pow(iTime,iTime/20.));
    vec2 z, c;
    vec2 uv = fragCoord.xy / iResolution.xy;

    c.x = 1.3333 * (uv.x - 0.5) * scale - center.x;
    c.y = (uv.y - 0.5) * scale - center.y;
    z = c;
    int it;
    for(int i = 0; i<iter; i++) {
        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (z.y * z.x + z.x * z.y) + c.y;

        if((x * x + y * y) > 4.0) break;
        z.x = x;
        z.y = y;
        it = i;
    }
	 fragColor = vec4(pal(float(iter-it)/float(iter),vec3(0.,0.,0.),vec3(1,1,1),vec3(4,2,1),vec3(0,0,0)),1.);
}
