float message(vec2 uv) { 

    // HELLO VISITOR. CLICK ON THE SOUND TAB TO SEE THE CODE FOR THE SOUND.

    // to alter in the icon
    uv-=vec2(1.,10.); if ((uv.x<0.)||(uv.x>=32.)||(uv.y<0.)||(uv.y>=3.)) return -1.; 
    int i=1, bit=int(pow(2.,floor(32.-uv.x)));
    if (int(uv.y)==2) i=  928473456/bit; // 00110111 01010111 01100001 01110000
    if (int(uv.y)==1) i=  626348112/bit; // 00100101 01010101 01010000 01010000
    if (int(uv.y)==0) i= 1735745872/bit; // 01100111 01110101 01100001 01010000
 	return float(i-2*(i/2));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //  if (iResolution.y<200.) to display only in the icon 
    if (iResolution.y<2000.) {float c=message(fragCoord.xy/8.);if(c>=0.){fragColor=vec4(c);return;}}


    vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

}

vec2 mainSound( in int samp,float time){
    float speed = 4.0;

    float loop = floor(time / (40.0 / speed));

    float cTime = mod(time, 40.0 / speed);
    float amplitude = 0.0;

	float fastStep = cTime * speed;
    float mediumStep = cTime * (speed / 2.0);

    float cPitch = -100.0;

    if (fastStep < 3.0) {
		cPitch = floor(fastStep) * 2.0;
		amplitude = mod(fastStep, 1.0);
    }

    if (fastStep > 3.0 && fastStep < 4.0) { 
    	cPitch = 5.0;
        amplitude = mod(fastStep, 1.0);
    }

    if (mediumStep >= 2.0 && mediumStep <= 4.0) {
        cPitch = 7.0;
        amplitude = mod(mediumStep, 1.0);
    }

    if ((mediumStep > 4.0 && mediumStep <= 6.0)
       || (mediumStep > 8.0 && mediumStep <= 10.0)) { 
    	cPitch = 9.0;
        amplitude = mod(fastStep, 1.0);
    }

    if ((mediumStep >= 6.0 && mediumStep <= 7.0)
       || (mediumStep >= 10.0 && mediumStep <= 11.0)) {
        cPitch = 7.0;
        amplitude = mod(mediumStep, 1.0);
    }

    if ((mediumStep > 12.0 && mediumStep <= 14.0)) { 
    	cPitch = 5.0;
        amplitude = mod(fastStep, 1.0);
    }

    if (mediumStep >= 14.0 && mediumStep <= 16.0) {
        cPitch = 4.0;
        amplitude = mod(mediumStep, 1.0);
    }

    if ((mediumStep > 16.0 && mediumStep <= 18.0)) { 
    	cPitch = 7.0;
        amplitude = mod(fastStep, 1.0);
    }

    if (mediumStep >= 18.0 && mediumStep <= 19.0) {
        cPitch = 0.0;
        amplitude = mod(mediumStep, 1.0);
    }

    float env;
    if (loop == 1.0) {
		env = cos(amplitude * 3.14);
    } else {
        env = sin(amplitude * 3.14);
    }

    float hz = pow(1.059463094359, cPitch - 3.0) * 440.0;
    return vec2( sin(6.2831* hz * time) * env);
}
