//see http://mathworld.wolfram.com/ComplexExponentiation.html
vec2 complexPower(vec2 z1, vec2 z2)
{
    float a2b2 = z1.x*z1.x+z1.y*z1.y;
    float t1 = pow(a2b2,z2.x/2.0)*exp(-z2.y*atan(z1.y,z1.x));
    float t2 = z2.x*atan(z1.y,z1.x)+0.5*z2.y*log(a2b2);
    return vec2(t1*cos(t2),t1*sin(t2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 z = (fragCoord.xy-iResolution.xy/2.0) / iResolution.xy*10.0;
    vec2 y = complexPower(z,z);
    vec4 color;
    color.x = (sin(y.x)+1.0)/2.0;
    color.y = (sin(y.y)+1.0)/2.0;
    color.z = (sin(length(y))+1.0)/2.0;
	fragColor = color;
}
