/**/ // 149 chars
     // NB: the for is a loop that step twice each i value. fract(-U) is doing both the original fract and nexts U=1.-U

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = abs( u+u-R ) / R.y;
    for( float i=0.,d=.1; i<=2.; i += d = .1-d )
        U = fract(-U),
        O += (i*i-i+.5) / 2e2 / abs( i* (U-U.y-i) + i - U ).x; }

