void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 samp = vec4(0.0);
    for (int y = -4; y < 4; y++)
        for (int x = -4; x < 4; x++)
            samp += texture(iChannel0, uv + vec2(x, y)/((1.0-abs(uv.x*2.0-1.0))*100.0));
        samp /= 8.0 * 8.0;
    fragColor = samp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    float s = iTime / 5.0;
    float r = uv.y <= uv.x * uv.x * uv.x * sin(s) + uv.x * uv.x * cos(s) + uv.x * cos(s) ? 1.0 : 0.0;
    float g = uv.y <= uv.x * uv.x * uv.x * cos(s) + uv.x * uv.x * sin(s) + uv.x * sin(s) ? 1.0 : 0.0;
    float b = uv.y <= uv.x * uv.x * uv.x * sin(s) + uv.x * uv.x * sin(s) + uv.x * sin(s) ? 1.0 : 0.0;
	fragColor = vec4(vec3(r, g, b),1.0);
}
