void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 col = texture( iChannel0, uv );

    float t = 0.4 * sin ( iTime * 1.5 );
    float d = t - uv.x+uv.y;
    if ( d < 0.0 )
    {
        // dark stripe
		if ( d > -0.02)
            col.rgb *= 0.9;

		fragColor = col;
        return;
    }


    // l is "luminance"
    float l =
        col.r * 0.2 +
        col.g * 0.7 +
        col.b * 0.1;

    vec3 gray = vec3(l,l,l);

    vec3 diff = col.rgb - gray;

    // very high saturation
    diff *= 2.0;
    vec3 saturated = clamp(gray + diff,0.0,1.0);

    // mix the high-saturation and the original
    col.rgb = mix(col.rgb,saturated, 0.5);

	fragColor = col;
}
