void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    vec4 color = texture(iChannel0, uv);

    float avg = (color.r + color.g + color.b)/3.0 ;

    color.r  = avg ;
    color.g  = avg ;
    color.b  = avg ;

    fragColor = color;

}
