float Circle(vec2 uv, vec2 p, float r, float blur) 
{
    float d = length(uv-p); //distance from the origin
    float c = smoothstep(r, r-blur, d);

    return c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //uv is being normalized to accomodate different screen sizes
	vec2 uv = fragCoord.xy / iResolution.xy; // 0 <> 1.

    uv -= 0.5;

    //screen isn't square, x is wider than y so need to mult by aspect ration
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0);

    float mask = Circle(uv, vec2(0.), 0.4, 0.05);

    mask -= Circle(uv, vec2(-0.1, 0.1), 0.07, 0.01);
    mask -= Circle(uv, vec2(0.1, 0.1), 0.07, 0.01);

    float mouth = Circle(uv, vec2(0., 0), .3, 0.02);
    mouth -= Circle(uv, vec2(0., 0.1), 0.3, 0.02);

    mask -= mouth;

    //col = vec3(mouth);
    col = vec3(1.0, 1., 0.) * mask;
	fragColor = vec4(col, 1.0);
}
