//261
void mainImage( out vec4 o, vec2 i )
{
    vec4 p=iResolution.xyxy*.5;
    p.xy-=i;
    p/=p.w;

    o-=o;

    for( float t=iTime*.1,
               s=sin(t), c=cos(t),
               e=atan(p.y,p.z=p.x*c+s)/1.57,
               d=(c-p.x*s)/length(p.yz),
               j=0.;

         j++<20.;

         o+=(p=texture( iChannel0, vec2(e-s-s, d+t*15.) ))
            *p.b*.17/abs(d*=.97+.03*c)
    );

}

