vec2 tr(vec2 p, vec2 res)
{
    p = -1.0+2.0*p/res.xy;
    p.x *= res.x/res.y;
    return p;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = vec2(cos(iTime),sin(iTime)) * iResolution.xy/4.0 + iResolution.xy/2.0;
	vec2 p = tr(fragCoord.xy - mouse + iResolution.xy/2.0,iResolution.xy);
    float angle = degrees(atan(p.y,p.x));

    float len = length(p)*6.0;

    float val = mod(angle + log(len)*60.0 + (step(mod(len,1.0),0.75)*2.0-1.0)*30.0*iTime,30.0);


    vec3 col = vec3(0.0);

    col = mix(vec3(1.0,0.5,0.0),vec3(0.0),step(mod(val,25.0),12.5));
    col = mix(col,1.0-col,smoothstep(0.6,0.65,1.0/len));


	fragColor = vec4(col,1.0);
}
