void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x / iResolution.y; // width:height aspect ratio
    float cntDist = distance(vec2(uv.x * ratio, uv.y), vec2(0.5 * ratio, 0.5));
    float colMult = smoothstep(0.0, cntDist * 2.0, 0.25);
	fragColor = vec4(vec3(uv,0.5+0.5*sin(iTime)) * colMult,1.0);
}
