void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float time = iTime;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 colors = vec3(0.);
    if (length(uv.y-fract(-time/10.)) < 0.01) {
        colors = texture(iChannel0,uv).rgb;
    } else {
        colors = texture(iChannel1,uv).rgb;
    }
    fragColor = vec4(colors,1.0);
}
