void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = 2.*q-1.0;
     vec3 col = vec3(0.1,0.2,0.3);

    float theta = 0.0;   
    vec2 pos = vec2(0.0,0.0);
    float f=0.0;
    f=distance(p,pos)*3.0;
    col += vec3(0.1,0.2,0.3)/(1.0-f);
    float rad = 0.3;


        for(int i=0;i<6;i++)
        {
            theta = 60.0 * float(i);

            pos = vec2(sin((theta*3.14/180.0)+iTime)*rad,cos((theta*3.14/180.0)+iTime)*rad);
            float r = distance(p,pos);
            f=r*3.2;
            col += vec3(0.1,0.2,0.3)/(1.0-f);
        }


	fragColor = vec4((abs(col)*0.1),1.0);



}
