vec4 myTexture(vec2 uv) {
    return vec4(texture(iChannel0,uv).rgb,/*alpha*/float(uv.x >= 0. && uv.y >= 0. && uv.x <= 1. && uv.y <= 1.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position = fragCoord.xy / iResolution.xy;
	vec2 uv = position;
    vec2 suv = vec2(((uv.x-0.5)*(iResolution.x / iResolution.y))+0.5,uv.y);

    #define PI 3.141592653589793238462643383279502884197169
    float atans = (atan(uv.x-0.5,uv.y-0.5)+PI)/(PI*2.);

    vec3 draw = vec3(0.);
    float time = iTime;
    for (float i = 0.; i < 120.; i++) { //There should be one without loops, but I didn't figure it out.
        vec2 uvi = vec2(((suv-0.5)*( i-fract(time+(atans*1.)) ))+0.5);
        draw = mix(draw,myTexture(uvi).rgb,myTexture(uvi).a);
    }

    fragColor = vec4(draw,1.0);
}
