float fun(float x, float y, float t, float v, float w){
    return sin(x*y * t)*0.022+sin(t*t)*0.001+cos(v+w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float x = fragCoord.x;
    float y = fragCoord.y;
    float t = iTime;
    float v = x+y-t;
    float w = x*y;
    float z = 3.0*x+4.0*y;

    float cr = fun(x,y,z,v,w);
    float cg = fun(x,y,t+1.0,v,w);
    float cb = fun(x,y,t+2.0,v,w);

    fragColor = vec4(cr, cg, cb, 1.0);

}
