//Standard Colors
vec4 RED = vec4(1.0, 0.0, 0.0, 1.0);
vec4 BLUE = vec4(0.0, 0.0, 1.0, 1.0);
vec4 GREEN = vec4(0.0, 1.0, 0.0, 1.0);
vec4 BLACK = vec4(0.0, 0.0, 0.0, 1.0);
vec4 WHITE = vec4(1.0, 1.0, 1.0, 1.0);

//House Shape
vec4 houseColor = vec4(.7, .5, .4, 1.0);
vec2 rectangleLocation = vec2(.5, .2);
float rectangleX = .8;
float rectangleY = .45;

//Chimney
vec4 chimneyColor = vec4(.8, .37, .25, 1.0);
vec2 chimLocation = vec2(.75, .4);
float chimX = .1;
float chimY = .25;

//Windows
vec4 windowColor = vec4(.25, .4, .7, 1.0);
vec2 wind1Location = vec2(.25, .1);
vec2 wind2Location = vec2(.25, .3);
vec2 wind3Location = vec2(.75, .3);
vec2 wind4Location = vec2(.75, .1);
float winX = .1;
float winY = .1;

//Door
vec4 doorColor = vec4(0.1, 0.1, 0.1, 1.0);
vec2 doorLocation = vec2(.5, .05);
float doorX = .12;
float doorY = .2;

//Layer Control 
//(True: Layer Appears, False: Layer Does not Appear)
//Controls the House Layer
bool showLayer1 = true;
//Controls the Chimeny Layer
bool showLayer2 = true;
//Controls the Windows Layer
bool showLayer3 = true;
//Controls the Door Layer
bool showLayer4 = true;
//Controls the Background Layer
bool showLayer5 = true;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.y *= iResolution.y / iResolution.x;

    //Layers Created
    vec4 layer1, layer2, layer3, layer4, layer5;

    vec4 background = texture(iChannel0, uv);
    layer5 = background; 

    //House
 	if ((uv.x > rectangleLocation.x - rectangleX / 2.0) && (uv.x < rectangleLocation.x + rectangleX / 2.0) 
    && (uv.y > rectangleLocation.y - rectangleY / 2.0) && (uv.y < rectangleLocation.y + rectangleY / 2.0))
    {
     layer1 = houseColor;   
    }

    //Chimney
    if ((uv.x > chimLocation.x - chimX / 2.0) && (uv.x < chimLocation.x + chimX / 2.0) 
    && (uv.y > chimLocation.y - chimY / 2.0) && (uv.y < chimLocation.y + chimY / 2.0))
    {
        layer2 = chimneyColor;
    }


    //Windows
    vec4 window1, window2, window3, window4, allWindows;

    //Window1
    if ((uv.x > wind1Location.x - winX / 2.0) && (uv.x < wind1Location.x + winX / 2.0) 
    && (uv.y > wind1Location.y - winY / 2.0) && (uv.y < wind1Location.y + winY / 2.0))
    {
        window1 = windowColor;
    }

    //Window2
    if ((uv.x > wind2Location.x - winX / 2.0) && (uv.x < wind2Location.x + winX / 2.0) 
    && (uv.y > wind2Location.y - winY / 2.0) && (uv.y < wind2Location.y + winY / 2.0))
    {
        window2 = windowColor;
    }

    //Window3
    if ((uv.x > wind3Location.x - winX / 2.0) && (uv.x < wind3Location.x + winX / 2.0) 
    && (uv.y > wind3Location.y - winY / 2.0) && (uv.y < wind3Location.y + winY / 2.0))
    {
        window3 = windowColor;
    }

    //Window4
    if ((uv.x > wind4Location.x - winX / 2.0) && (uv.x < wind4Location.x + winX / 2.0) 
    && (uv.y > wind4Location.y - winY / 2.0) && (uv.y < wind4Location.y + winY / 2.0))
    {
        window4 = windowColor;
    }

    //Combines all the windows into layer3
    allWindows = mix(window1, window2, window2.a);
    allWindows = mix(allWindows, window3, window3.a);
    allWindows = mix(allWindows, window4, window4.a);
    layer3 = allWindows;


    //Door
    if ((uv.x > doorLocation.x - doorX / 2.0) && (uv.x < doorLocation.x + doorX / 2.0) 
    && (uv.y > doorLocation.y - doorY / 2.0) && (uv.y < doorLocation.y + doorY / 2.0))
    {
        layer4 = doorColor;
    }

    //Mixed Layers created
    vec4  layer1and2;
    vec4 layer1and2and3;
    vec4 firstLayers;
    vec4 finalMix;

    //Layer Control implmentation
    //To determine if both house, and chimney show up
    //Both house, and chimney appear
    if(showLayer1 == true && showLayer2 == true)
    {
        layer1and2 = mix(layer2, layer1, layer1.a);
    }
    //Just the house appears
    else if(showLayer1 == false && showLayer2 == true)
    {
     layer1and2 = layer2;   
    }
    //Just the chimney appears
    else if(showLayer1 == true && showLayer2 == false)
    {
     layer1and2 = layer1;   
    }
    //neither of them appear
    else if(showLayer1 == false && showLayer2 == false)
    {
        layer1and2 = vec4(0.0, 0.0, 0.0, 0.0);
    }

    //To determine if windows appear or not
    //Windows appear
    if(showLayer3 == true)
    {
     layer1and2and3 = mix(layer1and2, layer3, layer3.a);    
    }
    //Windows do not appear
    else
    {
     layer1and2and3 = layer1and2;   
    }

    //To determine if the door appears or not
    //Door appears
    if(showLayer4 == true)
    {
     firstLayers = mix(layer1and2and3, layer4, layer4.a);   
    }
    //door does not appear
    else
    {
     firstLayers = layer1and2and3;   
    }

    //To determine if the background appears or not
    //background appears
    if(showLayer5 == true)
    {
     finalMix = mix(layer5, firstLayers, firstLayers.a);   
    }
    //background does not appear
    else
    {
     finalMix = firstLayers;   
    }

    //The final product
    fragColor = finalMix;

}
