#define DURATION 6.0
#define M_PI 3.14159265

float texNoise(vec2 n) {
    return texture(iChannel0, n, 0.0).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    float aspectRatio = iResolution.x / iResolution.y;
    p.x *= aspectRatio;
    vec2 center = vec2(0.5 * aspectRatio, 0.5);

    vec2 c = (p - center)* 2.0;
    float dist = length(c);

    float u_progress = mod(iDate.w, DURATION) / DURATION; // [0, 1]
    u_progress = abs(u_progress - 0.5) * 2.04;
    u_progress = min(u_progress, 1.0);
    u_progress = pow(u_progress, 0.3);

    float angleDistort = texNoise(vec2(dist, dist) * 0.25) * max(dist, u_progress) * 0.5;
    float angle = atan(c.x / c.y) / M_PI + 0.5 + angleDistort;

	float rSmokeInner = u_progress * u_progress * 0.7;
    float rSmokeOuter = 0.32 + sqrt(u_progress) * 0.6;

    float smoke = texNoise(vec2(fract(dist - u_progress * 0.4), angle));
    smoke = mix(smoke, 1.0, dist - rSmokeOuter); // heaviness
    float smokeFadeOutOuter = smoothstep(rSmokeOuter * 0.5, rSmokeOuter, dist);
    float smokeFadeOutInner = 0.15 + smoothstep(-0.01, max(rSmokeInner, 0.0), dist);
    float smokeFade = (1.0 - smoke) * (1.0 - smokeFadeOutOuter) * smokeFadeOutInner;

    float rFire = 0.02 + sqrt(u_progress) * 0.65;
    float fireFade = 1.0 - smoothstep(0.0, rFire, dist);

    vec3 col = mix(vec3(0.0), vec3(1.0), fireFade);
    col = mix(col, vec3(0.0), smokeFade);
	col += sin(u_progress * 16.0 * M_PI) * 0.03;
    col *= max(u_progress - 0.3, 0.0);

    fragColor = vec4(col, 1.0);
}
