void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // initiate with the uv 
	vec2 uv = fragCoord.xy / iResolution.xy;

    // the number of columns and tiles for the program
    float colTile = 8.0;
    float rowTile = 8.0;

	// use these for the tile colors
	vec4 whiteTile = vec4(1,1,1,1.0);
	vec4 blackTile = vec4(0,0,0,1.0);

    // math to find if it is in an area
   	float x = 0.5*(uv.x+1.0); // range [0,1]
    float y = 0.5*(uv.y+1.0); // range [0,1]

    // checks for areas to make black and if not then white
   	if ((mod(colTile*x, 1.0) < 0.5) && (mod(rowTile*y, 1.0) < 0.5) 
       || (mod(colTile*x, 1.0) > 0.5) && (mod(rowTile*y, 1.0) > 0.5))
         {
            fragColor = blackTile;
         }
         else
         {
            fragColor = whiteTile;
         } 
}
