const float c = 1., l = .2, la = 10.,           // celerity, wavelength, wave paquet length
            k = 6.28 / l, ka = 6.28 / la;       // wave number

float f( float x, float t) {                    // wave shape
    x -= c*t; x = min(0.,x);                    // phase
    if ( sin(ka/2.*x) > 0. ) x=0.;
    return  .1*l*(.5-.5*cos(ka*x)) * sin(k*x);  // wave paquet amplitude * wave
}

#define D(v)  smoothstep( 8./R.y, 0., abs ( v -U.y )  )      // draw curve

void mainImage( out vec4 O, vec2 U )
{
	vec2 R =  iResolution.xy;
    U = ( U+U - R ) / R.y;

    float y =       f(U.x, iTime),              // incident wave
          b =  -.8* f( 2.*R.x/R.y-U.x, iTime);  // bouncing wave.  Sign: -: string +: water wave

    O = vec4( D( y+b -.2) );                    // draw total wave
    O.gb +=   D( y   +.4);                      // incident wave
    O.b  +=   D( b   +.2);                      // bouncing wave
}
