float columns = 6.0;
float rows = 5.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 tex1 = texture(iChannel0, uv);
    vec4 tex2 = texture(iChannel1, uv);

    float drawRows = floor(uv.x * rows);
    float drawColumns = floor(uv.y * columns);

    float checkerboard = drawRows + drawColumns;

    if( mod(checkerboard, 2.0) == 0.0)
    {
        fragColor = tex1;
    }

    else
    {
        fragColor = tex2;
    }



}
