vec4 windowColor = vec4(0.0, 1.0, 1.0, 1.0);
vec2 windowPos1 = vec2(0.6, 0.3);
vec2 windowPos2 = vec2(0.3, 0.3);
vec2 windowDim = vec2(0.025, 0.03);

vec4 houseColor = vec4(1.0, 0.0, 0.0, 1.0);
vec2 housePos = vec2(0.5, 0.2);
vec2 houseDim = vec2(0.3, 0.2);

vec4 chimneyColor = vec4(1.0, 0.5, 0.0, 1.0);
vec2 chimneyPos = vec2(0.7, 0.5);
vec2 chimneyDim = vec2(0.05, 0.2);

float windowOn1 = 1.0;
float windowOn2 = 1.0;
float houseOn = 1.0;
float chimneyOn = 1.0;

vec4 BGColor = vec4(1.0, 1.0, 1.0, 1.0);

float createImage(vec2 uv, vec2 pos, vec2 dim, float aspectRatio){
	pos.y *= aspectRatio;
    float image = step(length(pos.x - uv.x), dim.x)*step(length(pos.y - uv.y), dim.y);
    return image;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float aspectRatio = iResolution.y/iResolution.x;

    uv.y *= aspectRatio;

    vec4 window1 = createImage(uv, windowPos1, windowDim, aspectRatio) * windowColor * windowOn1;
    vec4 window2 = createImage(uv, windowPos2, windowDim, aspectRatio) * windowColor * windowOn2;

    vec4 house = createImage(uv, housePos, houseDim, aspectRatio) * houseColor * houseOn;

    vec4 chimney = createImage(uv, chimneyPos, chimneyDim, aspectRatio) * chimneyColor * chimneyOn;

    vec4 composition = mix(BGColor, chimney, chimney.a);
     composition = mix(composition, house, house.a);
    composition = mix(composition, window1, window1.a);
    composition = mix(composition, window2, window2.a);
    fragColor = composition;
}
