void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord.xy;

    pos.x = pos.x / iResolution.x;
    pos.y = pos.y / iResolution.y;

    vec4 Color = vec4(0, 0, 0, 1.0);
   	Color.r = pos.x * abs(cos(iTime * 5.0));
    Color.g = 0.5 - pos.x * abs(sin(iTime * 5.0));
    Color.b = 0.6 - pos.y * abs(cos(iTime * 6.0));

    fragColor = Color;

}
