float Radius = 0.10;

vec4 BGColor = vec4(0.0,0.0,0.0,1.0);
vec4 Color1 = vec4(1.0,0.0,0.0,1.0);
vec4 Color2 = vec4(0.0,2.0,0.0,1.0);
vec4 Color3 = vec4(0.0,0.0,3.0,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos1 = vec2(0.5,0.5);
	vec2 pos2 = vec2(0.6,0.6);
	vec2 pos3 = vec2(0.4,0.4);

	 vec2 uv = fragCoord.xy / iResolution.xy;

 	//Correct for aspect ratio
    float aspect = iResolution.y / iResolution.x;
 	uv.y *= aspect;
    pos1.y*= aspect;
    pos2.y*= aspect;
    pos3.y*= aspect;

    pos1.y = sin(iTime);

    vec4 Layer0 = BGColor;
    vec4 Layer1, Layer2, Layer3;

    if(length(pos1 - uv) < Radius)
        Layer1 = Color1;
    else
        Layer1 = vec4(0.0,0.0,0.0,0.0);

    if(length(pos2 - uv) < Radius)
        Layer2 = Color2;
    else
        Layer2 = vec4(0.0,0.0,0.0,0.0);

    if(length(pos3 - uv) < Radius)
        Layer3 = Color3;
    else
        Layer3 = vec4(0.0,0.0,0.0,0.0);

    vec4 Color1_2Mix = mix(Layer2,Layer1,Layer1.a);
    vec4 AllColorMix = mix(Color1_2Mix,Layer3, Layer3.a);


    fragColor = AllColorMix;
}
