void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = 120. / 60. * iTime;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 cenuv = uv * 2.0 - vec2(1.0);
    cenuv.x *= iResolution.x / iResolution.y;
    float ymod1 = texture(iChannel0, vec2(uv.x, t * 0.02)).x;
    float ymod2 = texture(iChannel0, vec2(uv.x - 0.05, ymod1 * 0.05 - t * -0.02)).x;
    float ymod3 = texture(iChannel0, vec2(uv.x + 0.05, ymod2 * 0.05 + t * 0.03)).x;   
    float ymod = (ymod1 + ymod2 + ymod3) / 3.0 * 0.1;
    float bgy = uv.y + ymod;
    vec4 bg = vec4(step(mod(t + bgy * 30.0, 1.0), 0.2));
	float dsc = sqrt(cenuv.x * cenuv.x + cenuv.y * cenuv.y);
    vec4 fg = vec4(step(mod(t - dsc * 20.0, 1.0), 0.2));
    fragColor = mix(bg, fg, step(dsc, 0.6));
}
