void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int count = 5;

    // the following line compiles and executes fine
    // on my Mac in firefox and chrome but in safari results in:
    // "Loop-index cannot be compared with non-constant expression".
    // the error also occurs on iOS in all browsers,
    // but not in the 'shadertoy' app.
    //
    // presumably this is a bug in Apple's WebView component.
    for (int n = 0; n < count; ++n) {
    }

    // this has nothing to do with the apparent bug,
    // just something to look at.
    vec2 uv = 150.0 * (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float a = 0.0;
	a += sin(length(uv) * 0.12 + iTime * 1.0);
    a += sin(length(uv) * 0.21 - iTime * 1.1);
    a = a * 0.25 + 0.5;

    fragColor = vec4(vec3(a), 1.0);
}
