void mainImage(out vec4 fragColor, in vec2 fragCoord) {
 ivec2 uv = ivec2(floor(fragCoord / 2.));

 int mask = (int(iTime / 2.) & 7) << 2;

 vec3 bg = vec3(.3, .3, .3),
    warp = vec3(fragCoord/iResolution.xy, 1.),
    weft = vec3(.8, .8, .5);

 fragColor = vec4(((uv.x^uv.y) & mask) == 0
  ? 1 == ((uv.x ^ uv.x >> 1) & 1) ? warp : bg
  : 1 == ((uv.y ^ uv.y >> 1) & 1) ? weft : bg,
                  1.0);
}
