float Sphere(vec2 pos){
    return smoothstep(length(pos)+0.5,length(pos),0.51);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec4 col = vec4(0.0);
    vec2 uv = fragCoord/iResolution.y - 0.5; // Ha!
    vec4 bg = vec4(.11, 0.352, 0.729,1.0);
    vec4 fl = vec4(.4,.6,0.1,1.0)*smoothstep(0.0,-0.05,uv.y+0.15);
    vec2 sPos = uv+sin(iTime)*0.5;
    vec4 sol = vec4(0.9, 0.725, 0.2,1.0)+Sphere(sPos)+
        mix(.1,.0,sin(iTime)*5.0);


    col += mix(col,bg,bg.a);
    col = mix(col,sol,sol.a);

    col = mix(col,fl ,fl.a);

    fragColor = vec4(col);
}
