float map( vec3 p )
{
    return length(p) - 1.0;
}
float trace( vec3 ro, vec3 rd )
{
    float t = 0.0;
    for(int i = 0; i < 24; i++)
    {
        vec3 p = ro+t*rd;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}
vec4 light( in vec4 fragColor, vec3 uv )
{
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 rd = normalize(uv);
    vec3 ro = vec3(0.0, 0.0, -3.0);
    float t = trace(ro, rd);
    float fog = 1.0 / (1.0 + t * t * 0.1);
    vec3 fc = mix(vec3(fog),vec3(1.0,0.0,0.0),2.0);
    return vec4(vec3(min(uv.yyx,fc)), 1.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 uv = vec3(fragCoord.xy, 1.0).xyz / iResolution.xyz;
    vec4 skytex = texture(iChannel0, uv.xy);
    vec4 martx = texture(iChannel1, uv.xy*uv.zy*8.0);
    uv = uv * 2.0 - 0.9; //uv *= clamp(cos(iTime),1.7,2.0) - sin(iTime);
    uv.x *= length(uv.x);
    uv.xy *= length(uv.xy);
    if(uv.y < 0.7) {
        fragColor = mix(mix(light(fragColor, uv), normalize(reflect(vec4(uv.g, uv.g, uv.g, uv.g), vec4(0.2,0.0,1.0,1.0)))
                        ,2.0),martx,2.0);
    }
    if(uv.y < 1000.0 && uv.y > 0.7) {
        fragColor = mix(smoothstep(0.0, 0.55, vec4(0.4,0.6,0.9,1.0)),skytex,2.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = vec4(cos(uv.y)) + vec4(-0.01, 0.01, 0.9, 1.0);
}
