const float pi = 3.14152865;

float visuzl(float ind) {
    vec4 rvis = texture(iChannel1, vec2(ind,0.25));
    return (rvis.r+rvis.g+rvis.b)/3.0;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = vec4(0.0,0.0,0.0,0.0);
    vec2 position = gl_FragCoord.xy/iResolution.xy;
    vec2 pixsize = 1.0/iResolution.xy;
    vec2 mc = iMouse.xy/iResolution.xy;
    mc -= 0.5;
   	vec4 back = texture(iChannel0, position+vec2(mc.x, -pixsize.y*2.0));
    vec3 m = mix(color.rgb, back.rgb, 0.9);
    float amp = visuzl(position.x);
    if (gl_FragCoord.y <= 1.0) {
    	m = mix(m, vec3(0.9, 0.7, 0.8), pow(amp, 1.0));
    }
    color = vec4(m, 1.0);
    fragColor = color;
}
