vec3 tanh3(vec3 inp)
{
   return (vec3(1.0)-pow(vec3(2.71828),vec3(-2.0)*inp))/(vec3(1.0)+pow(vec3(2.71828),vec3(-2.0)*inp));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 diffs1 = vec3(0.0);
    vec2 uv = fragCoord / iResolution.xy;
    for(float i = 0.0; i<1.0; i+=0.01)
    {
        vec3 samp1 = texture(iChannel0,vec2(uv.x + iTime*0.01,iTime*0.01)).xyz;
        vec3 samp2 = texture(iChannel0,vec2(uv.y - iTime*0.01,iTime*0.001)).xyz;
        diffs1 += (samp1*samp1 - samp2*samp2);
    }

    diffs1 = sqrt(diffs1);

    vec3 tanhd = tanh3(diffs1);
    vec3 sampler = texture(iChannel0,uv+ 0.1*vec2(tanhd.x,tanhd.y)).xyz;

	fragColor = vec4(diffs1.xyz*0.3,1.0) + vec4(sampler,1.0);
}
