void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = texture(iChannel0, uv) * 0.7; // darken

    // Moving point
    vec2 light = vec2(0.5 * iTime, 0.5 * iTime);
    light.x = abs(sin(light.x));
    light.y = abs(sin(light.y));

    // Scale to a smaller size
    float dist = length(uv - light);
    float maxDist = 0.15;
    dist /= maxDist;

    // Invert
	dist = 1.0 - dist;

    float lightScale = 0.9;
    fragColor.xyz += dist * lightScale; 
}
