const vec3 keyColor = vec3(0.,162./255.,32./255.); // Green key color


void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    float r = 200.;
	vec2 resolution = vec2(r, r * (9. / 16.));

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = texture(iChannel0, uv);
    vec4 bgColor = texture(iChannel1, uv);

   	float diff = length(color.rgb - keyColor);

    if (diff < .37) {
        color.rgb = mix(bgColor.rgb, color.rgb, color.g/200.);
    }
	fragColor = color;
}
