float pi = 3.14159265;
float v = 2.11655518;

float newDist(float dist) {
    return (15.0 * pi * dist + 0.15 * sin(15.0 * pi * dist - 10.0 * iTime) / (dist)) / 15.0 / pi;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy / iResolution.xy;
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.y *= iResolution.y / iResolution.x;
    mouse.y *= iResolution.y / iResolution.x;

    float dist = sqrt((uv.x - mouse.x) * (uv.x - mouse.x) + (uv.y - mouse.y) * (uv.y - mouse.y));

    uv.xy = dist / newDist(dist) * (uv.xy - mouse.xy) + mouse.xy;

    uv.y = uv.y * iResolution.x / iResolution.y;

	fragColor = texture(iChannel0, uv);
}
