#define PI 3.14159265359

float sea(in float level, in float freq, in float amplitude, in vec2 uv)
{
    float val = cos((uv.x + iTime) * freq) / 2.0 + 0.5;
    val = val * amplitude + level;
    return 1.0 - smoothstep(val,val + 0.01,uv.y);
}

float circle(in vec2 pos, in float radius, in vec2 uv)
{
    float dist = distance(pos,uv);
    return  smoothstep(radius + 5., radius, dist);
}

float circleHalo(in vec2 pos, in float radius, in float haloSize,  in vec2 uv)
{
    float dist = distance(pos,uv);
    return 1.0 - smoothstep(radius, radius + haloSize,dist);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 sky1 = vec3(0.500,0.733,0.924);
    vec3 sky2 = vec3(0.949,0.141,0.312);

    vec3 sunFlame = vec3(1.0, 0.2, 0.1);
    vec3 sunColor = vec3(1.0, 1.0, 0.1);
    vec3 seaWaterDark = vec3(0.1, 0.4, 0.7);
    vec3 seaWaterMid = vec3(0.1, 0.5, 0.7);
    vec3 seaWaterClear = vec3(0.1, 0.6, 0.7);

    float sunRadius = 0.15 * iResolution.y;
    float oscillation = (sin(iTime * 0.1) * 0.5) + 0.5;
    vec2 sunPosition = vec2(0.5 ,0.05 + 0.7 * (1.0 - oscillation));

    vec3 skymix;
    skymix.r = sin(uv.x*PI);
    skymix.g = sin(oscillation * uv.y);
    skymix.b = sin(uv.x*PI);

    vec3 color = mix(sky1, sky2, skymix);

    color = mix(color,sunFlame,circleHalo(iResolution.xy * sunPosition, sunRadius, 0.30 * iResolution.y, fragCoord));
    color = mix(color,sunColor,circle( iResolution.xy * sunPosition, sunRadius, fragCoord));
    color = mix(color,seaWaterDark,sea(0.2,0.3, 0.08,uv));
    color = mix(color,seaWaterMid,sea(0.16,1.0, 0.06,uv));
    color = mix(color,seaWaterClear,sea(0.12,2.9, 0.02,uv));

    // Output to screen
    fragColor = vec4(color,1.0);
}
