void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y*32.0;

    float i     = uv.x;
    float fart  = texture(iChannel0,vec2(i ,0.25)).r;

    // Time varying pixel color
    vec3 butt = 0.5 + sin(iTime+fart*4.0+uv.y+vec3(0,2,4))
              + 1.0 + sin(iTime+uv.y+vec3(1,2,4))
              + 0.5 + cos(iTime+uv.x+vec3(0,1,4))
              + 1.0 + sin(iTime+uv.x+vec3(1,2,4));

    // Output to screen
    fragColor = vec4(butt,1.0);
}
