void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.);
    if (uv.x>0.5){
    	col = texture(iChannel0, uv).rgb;
        if (col.r>1.0){
            col = vec3(100., 100., 100.);
        } else {
        	col.g*=col.g;
    		col.b = 0.;
        }
    } else {
        col = texture(iChannel1, uv).rrr;
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

//the drawing buffer
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = iResolution.xy;
    vec2 uv = fragCoord/r.xy;
    vec4 col = vec4(0.);
    if (uv.x>0.45){
        vec2 mouse = iMouse.xy/r.xy;
        col = max(texture(iChannel0, uv-vec2(0.001, 0.01))-0.01-texture(iChannel1, uv).r*0.1, 0.);
        ivec2 f = ivec2(fragCoord);
        float focus = 4.;
        if (iResolution.x>1000.){
            focus = 2.;
        }
        float blur = (
            col*focus+
            texelFetch(iChannel0, ivec2(f.x, f.y+1), 0)+
            texelFetch(iChannel0, ivec2(f.x+1, f.y), 0)+
            texelFetch(iChannel0, ivec2(f.x-1, f.y), 0)+
            texelFetch(iChannel0, ivec2(f.x, f.y-1), 0)
        ).r/(4.+focus);
        col = vec4(blur);
        if (length(uv-mouse)<0.05&&iMouse.z>0.){
            col = vec4(1.01);
        }
        if (fragCoord.y<1.){
            col = vec4(1.01);
        }
    }
    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float b = 0.;
    for(float i = 0.; i<3.; i++){
        b+=texture(iChannel0, uv*pow(2., i)-vec2(0., iTime*0.2)).r*pow(0.8, i);
    }
    fragColor = vec4(b*b*b)*0.2;
}
