void mainImage( out vec4 O,  vec2 U ) {	
    O = texture(iChannel0, U/=iResolution.xy);   


/**/ // --- Shane's coloring scheme    

	// Fire palette - toned down with a touch of grayscale.
	O = mix(pow(O+.5*O.x, vec4(1, 3, 16, 1)), O, .1);

	// Spatial mixing term.
	U = sin(U*16. + cos(U.yx*8. + iDate.w));

	O = mix(O, O.yzxw, dot(U, vec2(.15)) + .3); // Color mix.
	O = mix(O, O.yzxw, dot(U, vec2(.25)) + .5); // Color mix.   
	O = sqrt(O);  // Rough gamma.   
/**/
}

// --- particles management

float Ldt = .3/60., Vdt = .1/60.;                 // probe rate, scroll rate

bool hit(vec2 P, out vec2 N) {                    // check for walls
  //N = vec2(0,sign(.5-P.y));  return abs(2.*P.y-1.) > .8; // test: straight corridor
    P.x +=  Vdt*float(iFrame);
    float L=6., A=.05* (.5-.5*cos(P.x));
    float y = P.y-A*sin(L*P.x),dy = -A*L*cos(L*P.x);
    N = normalize(vec2(dy,1)) *sign(.5-P.y); return abs(2.*y-1.) > .7;
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy, N;
    float t = iTime;
    O = texture(iChannel0, U/R);                         // O==0: recyclable
    if ((iMouse.z==0. && mod(t,4.)<2./60. || iMouse.z>0.) && O==vec4(0)) 
                                                  // particles initial position and direction
            O = vec4(.5,.5, // iMouse.xy/R.y, 
                     cos(6.283*fract(4e5*sin(1e4*U.x+1e2*U.y+t))+vec2(0,1.57)));

    O.xy += Ldt*O.zw;                             // particles kinematics
    if ( hit(O.xy, N) ) {
        O.xy -= Ldt*O.zw;
        O.zw = reflect(O.zw, N); 
        O.xy += Ldt*O.zw;
    }    
    O.x -= Vdt;                                   // coordinates in camera space
    if (O.x<0. || O.x>R.x/R.y) O-=O;              // out of screen: recycle
}

// --- draw particles trajectories

#define N 16
float Vdt = .1/60.;                                        // scroll rate

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy, V = U/R + vec2(Vdt,0);
    O = fract(V)==V ?.95*texture(iChannel0, V) : O-O;    // afterglow

	for (int j=0; j<N; j++)                                // splat particles
        for (int i=0; i<N; i++)
            V = U/R.y - texture(iChannel1,(.5+vec2(i,j))/R).xy,
            O += smoothstep(.01,0.,length(V));             // disks
          //O += 4e-5/length(V);                           // electric field
}
